import React, { Component } from 'react';
import { Carousel,Grid,Row,Col,Button,Pagination} from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
// import Pagination from 'react-js-pagination';
import '../App.css';
import Axios from 'axios';
class Dashboard extends Component {

    constructor(props) {
        super(props);
        this.state = {
            movie: [],
            film:[],
            activePage: 1,
            totalItemsCount:18,
            saldo:100000,
            count:0,
            isbought:[568709,516716],
        }

        this.handlePageChange = this.handlePageChange.bind(this);
        this.buyProcess = this.buyProcess.bind(this);
    }


    componentDidMount(){
        Axios.get('https://api.themoviedb.org/3/movie/now_playing?api_key=3fb35a358dae5edcaa04b777ba0b6ca3&language=en-US&region=ID')
        .then((response)=>{
            const film = response.data.results;
            this.setState({film})
        })

    }

    handlePageChange(pageNumber) {
        // console.log(`active page is ${pageNumber}`);
        Axios.get('https://api.themoviedb.org/3/movie/now_playing?api_key=3fb35a358dae5edcaa04b777ba0b6ca3&language=en-US&region=ID&page='+pageNumber)
        .then((response)=>{
            const film = response.data.results;

            this.setState({film,
                activePage:response.data.page,
                totalItemsCount:response.data.results.length
            })
        })
      }

      buyProcess(price){

          const newsaldo  = this.state.saldo-price
          this.setState({saldo:newsaldo})

      }

  render() {
    function Price(price) {
        if (price >= 0 && price <=3) {
          return 3500;
        }else if(price > 3 && price <=6){
            return 8250;
        }else if(price > 6 && price <=8){
            return 16350;
        }else if(price > 8 && price <=10){
            return 21250;
        }else{
            return 'Nan';
        }

      }

      function formatPrice(value) {
        let val = (value/1).toFixed().replace('.')
        return 'Rp. ' + val.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".")
    }
    return (
        // <h1>Dashboard</h1>
        <div>
            <div className='bg-container-layout'>
                <Carousel>
                    <Carousel.Item>
                        <img width='100%' alt='img' src="https://m-images.hooq.tv/resizer/resizer?service=singtel&surl=https://hooq-sanctuary.s3.amazonaws.com/assets/catalogs/SPOTLIGHT/c989cef654f6.jpg?&width=2500&height=977&scalingMode=aspectFill" />
                        <Carousel.Caption>
                        <h3>First slide label</h3>
                        <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                        </Carousel.Caption>
                    </Carousel.Item>
                    <Carousel.Item>
                        <img width='100%' alt='img' src="https://m-images.hooq.tv/resizer/resizer?service=singtel&surl=https://hooq-sanctuary.s3.amazonaws.com/assets/catalogs/SPOTLIGHT/c989cef654f6.jpg?&width=2500&height=977&scalingMode=aspectFill" />
                        <Carousel.Caption>
                        <h3>Second slide label</h3>
                        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                        </Carousel.Caption>
                    </Carousel.Item>
                    <Carousel.Item>
                        <img width='100%' alt='img' src="https://m-images.hooq.tv/resizer/resizer?service=singtel&surl=https://hooq-sanctuary.s3.amazonaws.com/assets/catalogs/SPOTLIGHT/c989cef654f6.jpg?&width=2500&height=977&scalingMode=aspectFill" />
                        <Carousel.Caption>
                        <h3>Third slide label</h3>
                        <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
                        </Carousel.Caption>
                    </Carousel.Item>
                    </Carousel>
            </div>
            <div className='bg-film-content'>
            <Grid>
                <h1 className='headlineFilm'>Sedang tayang</h1>
                        <Row >
                        {this.state.film.map((item) =>
                          <Col xs={12} md={2} xs={4} className='p-b-20 height-300'>
                              <NavLink to={'/detail/'+item.id+'-'+item.title.toLowerCase().replace(/\s+/g, '-')}>
                              <img 
                              alt='img'
                              src={'https://image.tmdb.org/t/p/w500/'+item.poster_path}
                              width='100%'
                              height={250}
                              />
                              </NavLink>
                              <p className='m-0 p-t-10 height-50'>{item.title}</p>
                              <p className='m-0 p-b-10 idr-color'>{formatPrice(Price(item.vote_average))}</p>
                              {this.state.isbought.includes(item.id)? 'anda sudah memilikinya' : <Button block className='buy-button' onClick={this.buyProcess.bind(this,Price(item.vote_average))}> Beli</Button>}
                          </Col>     
                        )}
                        {/* <Col xs={12} md={2} className='p-b-20'>
                              <img 
                              alt='img'
                              src={'https://image.tmdb.org/t/p/w500//nesDPKzwk4QnG6SpPjUhZXXNGyK.jpg'}
                              width='100%'
                              />
                              <p className='m-0 p-t-10'>BBB</p>
                              <p className='m-0 p-b-10 idr-color'>Rp. A</p>
                              <Button block className='buy-button'> Beli</Button>
                          </Col> */}

                        </Row>  
                        <div className='centerAlign'>
                        <Pagination className='customPg font-default'>
                            <Pagination.First />
                            <Pagination.Prev />
                            <Pagination.Item  active>{1}</Pagination.Item>

                            <Pagination.Item>{2}</Pagination.Item>
                            <Pagination.Next />
                            <Pagination.Last />
                        </Pagination>
                        {/* <Pagination className='users-pagination pull-right' bsSize='medium'
                            mmaxButtons={10} first last next prev boundaryLinks
                            items={pages} activePage={current_page} /> */}
                            {/* <div>
                                <Pagination
                                activePage={this.state.activePage}
                                totalItemsCount={this.state.totalItemsCount}
                                pageRangeDisplayed={this.state.pageRangeDisplayed}
                                onChange={this.handlePageChange}
                                />
                            </div> */}
                        </div>

            </Grid>
            </div>
        </div>
    );
  }
}

export default Dashboard;
