import React, { Component } from 'react';
import { Carousel,Grid,Row,Col,Button,Pagination} from 'react-bootstrap';
import '../App.css'
import Axios from 'axios';
class Dashboard extends Component {

    constructor(props) {
        super(props);
        this.state = {
            movie: [
                ]
        }
    }

    componentDidMount(){
        Axios.get('https://api.themoviedb.org/3/movie/now_playing?api_key=3fb35a358dae5edcaa04b777ba0b6ca3&language=en-US&region=ID')
        .then((response)=>{
            for(let i;i<19;i++){
               console.log(i)
            }
        })

        console.log(this.state.movie)
    }
  render() {
    return (
        // <h1>Dashboard</h1>
        <div>
            <div className='bg-container-layout'>
                <Carousel>
                    <Carousel.Item>
                        <img width='100%' alt='img' src="https://m-images.hooq.tv/resizer/resizer?service=singtel&surl=https://hooq-sanctuary.s3.amazonaws.com/assets/catalogs/SPOTLIGHT/c989cef654f6.jpg?&width=2500&height=977&scalingMode=aspectFill" />
                        <Carousel.Caption>
                        <h3>First slide label</h3>
                        <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                        </Carousel.Caption>
                    </Carousel.Item>
                    <Carousel.Item>
                        <img width='100%' alt='img' src="https://m-images.hooq.tv/resizer/resizer?service=singtel&surl=https://hooq-sanctuary.s3.amazonaws.com/assets/catalogs/SPOTLIGHT/c989cef654f6.jpg?&width=2500&height=977&scalingMode=aspectFill" />
                        <Carousel.Caption>
                        <h3>Second slide label</h3>
                        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                        </Carousel.Caption>
                    </Carousel.Item>
                    <Carousel.Item>
                        <img width='100%' alt='img' src="https://m-images.hooq.tv/resizer/resizer?service=singtel&surl=https://hooq-sanctuary.s3.amazonaws.com/assets/catalogs/SPOTLIGHT/c989cef654f6.jpg?&width=2500&height=977&scalingMode=aspectFill" />
                        <Carousel.Caption>
                        <h3>Third slide label</h3>
                        <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
                        </Carousel.Caption>
                    </Carousel.Item>
                    </Carousel>
            </div>
            <div className='bg-film-content'>
            <Grid>
                <h1 className='headlineFilm'>Sedang tayang</h1>
                <Row>
                    <Col xs={12}>
                        <Row >
                        {this.state.movie.map((item, key) =>
                              <Col xs={12} md={2} className='p-b-20'>
                              <img 
                              alt='img'
                              src={item.image}
                              width='100%'
                              />
                              <p className='m-0 p-t-10'>{item.name}</p>
                              <p className='m-0 p-b-10 idr-color'>Rp. {item.price}</p>
                              <Button block className='buy-button'> Beli</Button>
                          </Col>     
                        )}
                        </Row>  
                        <div className='centerAlign'>
                        <Pagination className='customPg font-default'>
                            <Pagination.First />
                            <Pagination.Prev />
                            <Pagination.Item  active>{1}</Pagination.Item>

                            <Pagination.Item>{2}</Pagination.Item>
                            <Pagination.Next />
                            <Pagination.Last />
                        </Pagination>
                        </div>
                    </Col>
                </Row>
            </Grid>
            </div>
        </div>
    );
  }
}

export default Dashboard;
